import React from 'react';
import './styles.css'; 

class App extends React.Component {
  // Add constructor here if you plan to use state

  handleSubmit = (event) => {
    event.preventDefault();
    // Here you'll handle the submission of the form
    // For example, you can log the form data or send it to the server
  };

  render() {
    return (
      <div className="container">
        <h1>Restaurant Admin Portal</h1>
        <form onSubmit={this.handleSubmit}>
          <input type="text" id="name" placeholder="Name" required />
          <input type="text" id="location" placeholder="Location" required />
          <input type="text" id="cuisine" placeholder="Cuisine (comma-separated)" required />
          <input type="text" id="menu" placeholder="Menu URL" required />
          <input type="text" id="openingHours" placeholder="Opening Hours" required />
          <button type="submit">Add New Restaurant</button>
        </form>
      </div>
    );
  }
}

export default App;
