// RestaurantList.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        // Match the endpoint with the one specified in your Postman
        const response = await fetch('http://localhost:5000/restaurants');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        // Sort the data alphabetically by the restaurant name
        const sortedData = data.sort((a, b) => a.name.localeCompare(b.name));
        setRestaurants(sortedData);
      } catch (error) {
        console.error("There was a problem fetching the restaurant data:", error);
      }
    };
    
    

    fetchRestaurants();
  }, []);

  return (
    <div>
      <h1>All Restaurants</h1>
      <ul>
        {restaurants.map(restaurant => (
          <li key={restaurant._id}> {/* Make sure to use the correct unique key from your data, often it's _id for MongoDB documents */}
            <Link to={`/restaurant/${restaurant._id}`}>{restaurant.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RestaurantList;
